import React from 'react';
import { useCart } from '../context/CartContext';
import { Link } from 'react-router-dom';

const CartDrawer = () => {
  const { cart, updateQuantity, removeFromCart } = useCart();

  return (
    <div className="fixed top-0 right-0 w-full max-w-sm h-full bg-purple-800 p-6 shadow-lg z-50 text-white">
      <h2 className="text-3xl font-bold text-cyan-500 mb-4">Your Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div className="space-y-6">
          {cart.map((item) => (
            <div key={item.id} className="flex items-center justify-between bg-purple-700 p-4 rounded-lg shadow-md">
              <div>
                <h3 className="text-xl font-semibold text-cyan-300">{item.name}</h3>
                <p className="text-lg text-cyan-400">${item.price.toFixed(2)}</p>
                <div className="flex items-center mt-2 space-x-2">
                  <button
                    onClick={() => updateQuantity(item.id, item.quantity - 1)}
                    className="px-3 py-1 bg-cyan-500 text-navy font-bold rounded-full"
                    disabled={item.quantity === 1}
                  >
                    -
                  </button>
                  <span className="text-lg font-semibold">{item.quantity}</span>
                  <button
                    onClick={() => updateQuantity(item.id, item.quantity + 1)}
                    className="px-3 py-1 bg-cyan-500 text-navy font-bold rounded-full"
                  >
                    +
                  </button>
                </div>
              </div>
              <button
                onClick={() => removeFromCart(item.id)}
                className="text-red-600 hover:text-red-400"
              >
                Remove
              </button>
            </div>
          ))}
          <Link to="/checkout">
            <button className="w-full bg-cyan-500 text-navy font-bold py-3 rounded-lg mt-4 hover:bg-cyan-400 transition-all duration-300">
              Proceed to Checkout
            </button>
          </Link>
        </div>
      )}
    </div>
  );
};

export default CartDrawer;
