import React, { useState } from 'react';
import { useCart } from '../context/CartContext';

const Checkout = () => {
  const { cart } = useCart(); // Get cart from context
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    email: '',
    address: '',
    city: '',
    state: '',
    zip: '',
  });
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCustomerInfo((prevInfo) => ({ ...prevInfo, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    console.log('Submitting Order...');
    console.log('Customer Info:', customerInfo);
    console.log('Cart:', cart);

    try {
      const response = await fetch('http://localhost:5000/api/checkout', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ customerInfo, cart }),
      });

      const data = await response.json();

      if (response.ok) {
        console.log('Order Submitted:', data);
        setIsSubmitted(true);
      } else {
        console.error('Error during checkout:', data.error);
        alert(`Checkout failed: ${data.error}`);
      }
    } catch (error) {
      console.error('Error submitting order:', error);
      alert('An error occurred during checkout.');
    }
  };

  const calculateTotal = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0).toFixed(2);
  };

  return (
    <div className="max-w-4xl mx-auto p-8 bg-purple-800 text-white rounded-lg mt-8">
      <h1 className="text-3xl font-bold text-cyan-500 mb-6">Checkout</h1>

      {isSubmitted ? (
        <div className="text-center">
          <h2 className="text-2xl font-semibold text-cyan-300">Thank you for your order!</h2>
          <p className="text-lg text-gray-300">Your order has been submitted and is being processed.</p>
        </div>
      ) : (
        <form onSubmit={handleSubmit} className="space-y-6">
          <h2 className="text-2xl font-semibold mb-4">Customer Information</h2>
          <input
            type="text"
            name="name"
            placeholder="Full Name"
            value={customerInfo.name}
            onChange={handleChange}
            required
            className="w-full p-3 rounded-lg"
          />
          <input
            type="email"
            name="email"
            placeholder="Email Address"
            value={customerInfo.email}
            onChange={handleChange}
            required
            className="w-full p-3 rounded-lg"
          />
          <input
            type="text"
            name="address"
            placeholder="Street Address"
            value={customerInfo.address}
            onChange={handleChange}
            required
            className="w-full p-3 rounded-lg"
          />
          <input
            type="text"
            name="city"
            placeholder="City"
            value={customerInfo.city}
            onChange={handleChange}
            required
            className="w-full p-3 rounded-lg"
          />
          <input
            type="text"
            name="state"
            placeholder="State"
            value={customerInfo.state}
            onChange={handleChange}
            required
            className="w-full p-3 rounded-lg"
          />
          <input
            type="text"
            name="zip"
            placeholder="ZIP Code"
            value={customerInfo.zip}
            onChange={handleChange}
            required
            className="w-full p-3 rounded-lg"
          />

          <h2 className="text-2xl font-semibold mb-4">Order Summary</h2>
          <div className="space-y-4">
            {cart.map((item) => (
              <div key={item.id} className="flex justify-between items-center bg-purple-700 p-4 rounded-lg shadow-md">
                <div>
                  <h3 className="text-lg font-semibold text-cyan-300">{item.name}</h3>
                  <p className="text-cyan-400">${item.price.toFixed(2)}</p>
                  <p className="text-gray-300">Quantity: {item.quantity}</p>
                </div>
                <p className="text-cyan-400 font-medium">${(item.price * item.quantity).toFixed(2)}</p>
              </div>
            ))}
          </div>
          <div className="text-right mt-4 text-lg font-semibold text-cyan-300">
            Total: ${calculateTotal()}
          </div>

          <button
            type="submit"
            className="w-full py-3 mt-6 bg-purple-600 text-white font-bold rounded-lg shadow-lg hover:shadow-xl hover:bg-purple-700 transform transition-all duration-300 hover:scale-105"
          >
            Place Order
          </button>
        </form>
      )}
    </div>
  );
};

export default Checkout;
