// src/components/ProductCard.js
import React from 'react';
import { Link } from 'react-router-dom';

const ProductCard = ({ product }) => {
  return (
    <div className="bg-purple-800 rounded-lg shadow-lg overflow-hidden transition-transform transform hover:scale-105">
      <img
        src={product.image}
        alt={product.name}
        className="w-full h-48 object-cover"
      />
      <div className="p-4">
        <h2 className="text-2xl font-semibold text-cyan-300">
          {product.name}
        </h2>
        <p className="text-cyan-400 text-lg font-medium mb-2">
          ${product.price.toFixed(2)}
        </p>
        <Link
          to={`/product/${product.id}`}
          className="text-cyan-300 hover:text-cyan-500 underline transition-all duration-200"
        >
          View Details
        </Link>
      </div>
    </div>
  );
};

export default ProductCard;
