// src/pages/ShopPage.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { products } from '../data';
import SearchBar from '../components/SearchBar';

const ShopPage = () => {
  const [searchTerm, setSearchTerm] = useState('');

  const filteredProducts = products.filter((product) =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  return (
    <section className="max-w-6xl mx-auto p-8 text-white">
      <h1 className="text-3xl font-bold mb-6 text-cyan-500">Shop Our Collection</h1>
      <SearchBar searchTerm={searchTerm} onSearchChange={handleSearchChange} />
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
        {filteredProducts.map((product) => (
          <div
            key={product.id}
            className="bg-purple-800 rounded-lg shadow-lg overflow-hidden transition-transform transform hover:scale-105"
          >
            <img src={product.image} alt={product.name} className="w-full h-48 object-cover" />
            <div className="p-4">
              <h2 className="text-2xl font-semibold text-cyan-300">{product.name}</h2>
              <p className="text-cyan-400 text-lg font-medium mb-2">{product.price}</p>
              <Link
                to={`/product/${product.id}`}
                className="text-cyan-300 hover:text-cyan-500 underline transition-all duration-200"
              >
                View Details
              </Link>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default ShopPage;
