// src/components/Hero.js
import React from 'react';

const Hero = () => {
  return (
    <section className="bg-pink text-navy py-20 flex flex-col items-center text-center">
      <h2 className="text-4xl font-extrabold mb-4">Welcome to Dad Hat Hub</h2>
      <p className="text-lg mb-8 max-w-md">
        Discover the best dad hats around. Simple, stylish, and made just for you.
      </p>
      <a href="/shop" className="px-8 py-3 bg-purple text-white rounded interactive-button">
        Shop Now
      </a>
    </section>
  );
};

export default Hero;
