// src/components/ProductCard.js
import React from 'react';
import { Link } from 'react-router-dom';

const ProductCard = ({ product }) => {
  return (
    <div className="bg-pink rounded-xl shadow-lg overflow-hidden transform transition duration-300 hover:scale-105 hover:shadow-2xl">
      <img src={product.image} alt={product.name} className="h-64 w-full object-cover" />
      <div className="p-5 text-center">
        <h3 className="text-2xl font-semibold text-navy mb-2">{product.name}</h3>
        <p className="text-xl font-medium text-navy mb-4">{product.price}</p>
        <Link to={`/product/${product.id}`}>
          <button className="px-6 py-2 bg-purple text-white font-semibold rounded-full hover:bg-purple-700 transition-all duration-300 transform hover:scale-110">
            View Details
          </button>
        </Link>
      </div>
    </div>
  );
};

export default ProductCard;
