// src/components/ProductCard.js
import React from 'react';
import { Link } from 'react-router-dom';

const ProductCard = ({ product }) => {
  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden transform transition-transform duration-300 hover:scale-105">
      <img
        src={product.image}
        alt={product.name}
        className="h-64 w-full object-cover"
      />
      <div className="p-4">
        <h3 className="text-xl font-semibold text-gray-800">
          {product.name}
        </h3>
        <p className="text-lg text-gray-600 mt-2">
          ${product.price.toFixed(2)}
        </p>
        <Link to={`/product/${product.id}`}>
          <button className="mt-4 w-full py-2 bg-blue-600 text-white font-medium rounded hover:bg-blue-700 transition-colors duration-300">
            View Details
          </button>
        </Link>
      </div>
    </div>
  );
};

export default ProductCard;
