import React, { useState } from 'react';
import { useCart } from '../context/CartContext';

const Checkout = () => {
  const { cart, getTotalPrice } = useCart(); // Access cart and total price from context
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    email: '',
    address: '',
    city: '',
    state: '',
    zip: '',
  });
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCustomerInfo((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    console.log('Submitting Order:', { ...customerInfo, cart });

    if (cart.length === 0) {
      alert('Your cart is empty. Please add items to proceed.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/checkout', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ customerInfo, cart }),
      });

      const data = await response.json();

      if (response.ok) {
        console.log('Order Submitted:', data);
        setIsSubmitted(true);
      } else {
        console.error('Checkout Error:', data.error);
        alert(`Failed to place order: ${data.error}`);
      }
    } catch (error) {
      console.error('Error submitting order:', error.message);
      alert('An error occurred. Please try again.');
    }
  };

  const calculateTotal = () => getTotalPrice().toFixed(2);

  return (
    <div className="max-w-4xl mx-auto p-8 bg-gray-100 text-gray-800 rounded-lg mt-8 shadow-md">
      <h1 className="text-3xl font-bold text-gray-700 mb-6">Checkout</h1>

      {isSubmitted ? (
        <div className="text-center">
          <h2 className="text-2xl font-semibold text-green-600">Thank you for your order!</h2>
          <p className="text-lg text-gray-600">Your order has been placed successfully. You will receive an email with tracking details soon.</p>
        </div>
      ) : (
        <form onSubmit={handleSubmit}>
          {/* Order Summary */}
          <div className="mb-8">
            <h2 className="text-2xl font-semibold mb-4">Order Summary</h2>
            <div className="bg-white shadow rounded-lg p-4 space-y-4">
              {cart.map((item) => (
                <div key={item.id} className="flex items-center space-x-4">
                  <img
                    src={item.image || 'https://via.placeholder.com/100'}
                    alt={item.name}
                    className="w-20 h-20 object-contain rounded-lg border"
                  />
                  <div className="flex-grow">
                    <h3 className="text-lg font-medium">{item.name}</h3>
                    <p className="text-sm text-gray-600">Quantity: {item.quantity}</p>
                  </div>
                  <p className="text-lg font-semibold">${(item.price * item.quantity).toFixed(2)}</p>
                </div>
              ))}
              <div className="border-t border-gray-300 pt-4 text-right text-lg font-semibold">
                Total: ${calculateTotal()}
              </div>
            </div>
          </div>

          {/* Customer Information */}
          <div className="mb-8">
            <h2 className="text-2xl font-semibold mb-4">Customer Information</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <input
                type="text"
                name="name"
                placeholder="Full Name"
                value={customerInfo.name}
                onChange={handleChange}
                required
                className="w-full p-3 rounded-lg border border-gray-300 bg-white focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
              <input
                type="email"
                name="email"
                placeholder="Email Address"
                value={customerInfo.email}
                onChange={handleChange}
                required
                className="w-full p-3 rounded-lg border border-gray-300 bg-white focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
              <input
                type="text"
                name="address"
                placeholder="Street Address"
                value={customerInfo.address}
                onChange={handleChange}
                required
                className="w-full p-3 rounded-lg border border-gray-300 bg-white focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
              <input
                type="text"
                name="city"
                placeholder="City"
                value={customerInfo.city}
                onChange={handleChange}
                required
                className="w-full p-3 rounded-lg border border-gray-300 bg-white focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
              <input
                type="text"
                name="state"
                placeholder="State"
                value={customerInfo.state}
                onChange={handleChange}
                required
                className="w-full p-3 rounded-lg border border-gray-300 bg-white focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
              <input
                type="text"
                name="zip"
                placeholder="Zip Code"
                value={customerInfo.zip}
                onChange={handleChange}
                required
                className="w-full p-3 rounded-lg border border-gray-300 bg-white focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
            </div>
          </div>

          {/* Submit Button */}
          <button
            type="submit"
            className="w-full py-3 bg-purple-500 text-white font-bold rounded-lg shadow-lg hover:bg-purple-600 focus:outline-none focus:ring-4 focus:ring-purple-300 transition-all"
          >
            Place Order
          </button>
        </form>
      )}
    </div>
  );
};

export default Checkout;
