// src/components/Checkout.js
import React, { useState } from 'react';
import { loadStripe } from '@stripe/stripe-js';
import { useCart } from '../context/CartContext';

// Load Stripe with your publishable key
const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY);

const Checkout = () => {
  const { cart } = useCart();
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    email: '',
    address: '',
    city: '',
    state: '',
    zip: '',
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  // Calculate totals
  const subTotal = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);
  const shipping = 500; // Example shipping cost in cents ($5.00)
  const taxRate = 0.07; // Example tax rate (7%)
  const tax = Math.round(subTotal * taxRate);
  const total = subTotal + shipping + tax;

  // Handles input changes for customer information
  const handleChange = (e) => {
    const { name, value } = e.target;
    setCustomerInfo((prev) => ({ ...prev, [name]: value }));
  };

  // Submits the checkout request
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setErrorMessage('');

    try {
      const response = await fetch('http://localhost:5000/api/stripe/create-checkout-session', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          cart: cart.map((item) => ({
            id: item.id,
            name: item.name,
            price: item.price,
            quantity: item.quantity,
            variant_id: item.variant_id,
            thumbnail_url: item.thumbnail_url,
          })),
          customerInfo,
        }),
      });

      if (!response.ok) {
        throw new Error(`API returned status ${response.status}`);
      }

      const { id } = await response.json();
      const stripe = await stripePromise;
      const { error } = await stripe.redirectToCheckout({ sessionId: id });

      if (error) {
        setErrorMessage(error.message);
      }
    } catch (error) {
      console.error('Error during checkout:', error.message);
      setErrorMessage('An error occurred during checkout. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="max-w-6xl mx-auto mt-10 p-6">
      {/* Checkout Header */}
      <div className="relative bg-primary text-textcolor rounded-lg overflow-hidden mb-8">
        {/* Optional Background Image */}
        {/* <img src="/path/to/your/image.jpg" alt="Checkout" className="absolute inset-0 w-full h-full object-cover opacity-20" /> */}
        {/* Powered by Stripe */}
        <div className="absolute top-4 right-4 flex items-center">
          <p className="text-sm mr-2">Payments powered by</p>
          <a
            href="https://stripe.com"
            target="_blank"
            rel="noopener noreferrer"
          >
            <img src="/path/to/stripe-logo.png" alt="Stripe" className="h-6" />
          </a>
        </div>
        <div className="relative p-8 text-center">
          <h1 className="text-4xl font-bold mb-2">Secure Checkout</h1>
          <p className="text-lg">Complete your purchase by providing your payment details.</p>
        </div>
        {/* Progress Indicator */}
        <div className="absolute bottom-0 left-0 w-full">
          <div className="flex justify-center items-center mb-2">
            <div className="flex items-center">
              {/* Step 1 */}
              <div className="flex items-center">
                <div className="w-6 h-6 rounded-full bg-accent text-primary flex items-center justify-center font-bold text-sm">
                  1
                </div>
                <span className="text-sm ml-2 text-textcolor">Cart</span>
              </div>
              <div className="h-1 w-12 bg-accent mx-2"></div>
              {/* Step 2 */}
              <div className="flex items-center">
                <div className="w-6 h-6 rounded-full bg-accent text-primary flex items-center justify-center font-bold text-sm">
                  2
                </div>
                <span className="text-sm ml-2 text-textcolor">Checkout</span>
              </div>
              <div className="h-1 w-12 bg-textcolor mx-2"></div>
              {/* Step 3 */}
              <div className="flex items-center">
                <div className="w-6 h-6 rounded-full bg-textcolor text-primary flex items-center justify-center font-bold text-sm">
                  3
                </div>
                <span className="text-sm ml-2 text-textcolor">Confirmation</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {errorMessage && (
        <p className="text-red-500 mb-4">
          <strong>Error:</strong> {errorMessage}
        </p>
      )}
      <div className="flex flex-col lg:flex-row gap-8">
        {/* Cart Summary */}
        <div className="w-full lg:w-1/2 bg-primary text-background p-6 rounded-lg">
          <h2 className="text-2xl font-bold mb-4 text-textcolor">Order Summary</h2>
          <div className="space-y-4">
            {cart.map((item) => (
              <div key={item.id} className="flex items-center">
                <img
                  src={item.thumbnail_url || 'https://via.placeholder.com/80'}
                  alt={item.name}
                  className="w-16 h-16 object-cover rounded mr-4"
                />
                <div className="flex-1">
                  <h3 className="text-lg font-semibold text-textcolor">{item.name}</h3>
                  <p className="text-textcolor">
                    Quantity: {item.quantity}
                  </p>
                </div>
                <p className="text-lg font-bold text-textcolor">
                  ${(item.price * item.quantity / 100).toFixed(2)}
                </p>
              </div>
            ))}
          </div>
          <hr className="my-4 border-textcolor" />
          <div className="space-y-2 text-textcolor">
            <div className="flex justify-between">
              <span>Subtotal</span>
              <span>${(subTotal / 100).toFixed(2)}</span>
            </div>
            <div className="flex justify-between">
              <span>Shipping</span>
              <span>${(shipping / 100).toFixed(2)}</span>
            </div>
            <div className="flex justify-between">
              <span>Tax (7%)</span>
              <span>${(tax / 100).toFixed(2)}</span>
            </div>
            <div className="flex justify-between font-bold text-xl">
              <span>Total</span>
              <span>${(total / 100).toFixed(2)}</span>
            </div>
          </div>
        </div>

        {/* Checkout Form */}
        <div className="w-full lg:w-1/2 bg-primary text-background p-6 rounded-lg">
          <h2 className="text-2xl font-bold mb-4 text-textcolor">Billing Details</h2>
          <form onSubmit={handleSubmit} className="space-y-4">
            {/* Customer Information Fields */}
            <input
              type="text"
              name="name"
              placeholder="Full Name"
              value={customerInfo.name}
              onChange={handleChange}
              required
              className="w-full p-3 border border-secondary rounded bg-secondary text-textcolor placeholder-textcolor focus:outline-none focus:border-accent focus:ring-1 focus:ring-accent"
            />
            <input
              type="email"
              name="email"
              placeholder="Email Address"
              value={customerInfo.email}
              onChange={handleChange}
              required
              className="w-full p-3 border border-secondary rounded bg-secondary text-textcolor placeholder-textcolor focus:outline-none focus:border-accent focus:ring-1 focus:ring-accent"
            />
            <input
              type="text"
              name="address"
              placeholder="Street Address"
              value={customerInfo.address}
              onChange={handleChange}
              required
              className="w-full p-3 border border-secondary rounded bg-secondary text-textcolor placeholder-textcolor focus:outline-none focus:border-accent focus:ring-1 focus:ring-accent"
            />
            <div className="flex flex-col md:flex-row md:space-x-4">
              <input
                type="text"
                name="city"
                placeholder="City"
                value={customerInfo.city}
                onChange={handleChange}
                required
                className="w-full md:w-1/2 p-3 border border-secondary rounded bg-secondary text-textcolor placeholder-textcolor focus:outline-none focus:border-accent focus:ring-1 focus:ring-accent mb-4 md:mb-0"
              />
              <input
                type="text"
                name="state"
                placeholder="State"
                value={customerInfo.state}
                onChange={handleChange}
                required
                className="w-full md:w-1/4 p-3 border border-secondary rounded bg-secondary text-textcolor placeholder-textcolor focus:outline-none focus:border-accent focus:ring-1 focus:ring-accent mb-4 md:mb-0"
              />
              <input
                type="text"
                name="zip"
                placeholder="Zip Code"
                value={customerInfo.zip}
                onChange={handleChange}
                required
                className="w-full md:w-1/4 p-3 border border-secondary rounded bg-secondary text-textcolor placeholder-textcolor focus:outline-none focus:border-accent focus:ring-1 focus:ring-accent"
              />
            </div>

            {/* Submit Button */}
            <button
              type="submit"
              disabled={isSubmitting}
              className={`w-full py-3 text-primary font-bold ${
                isSubmitting ? 'bg-gray-500 cursor-not-allowed' : 'bg-background hover:bg-accent'
              } rounded transition-all duration-300`}
            >
              {isSubmitting ? 'Processing...' : 'Pay Now'}
            </button>

            {/* Powered by Stripe */}
            <div className="mt-4 text-center">
              <p className="text-sm text-textcolor">
                Payments powered by{' '}
                <a
                  href="https://stripe.com"
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-accent hover:underline"
                >
                  Stripe
                </a>
              </p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
