import React from 'react';
import { useCart } from '../context/CartContext';

const Checkout = () => {
  const { cart, updateQuantity } = useCart();

  const calculateTotalPrice = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0).toFixed(2);
  };

  return (
    <section className="max-w-5xl mx-auto p-8 text-white">
      <h1 className="text-4xl font-bold text-cyan-500 mb-8">Checkout</h1>
      {cart.length === 0 ? (
        <p>Your cart is empty. Go back to shop and add some products!</p>
      ) : (
        <>
          <div className="space-y-6 mb-8">
            {cart.map((item) => (
              <div key={item.id} className="flex items-center justify-between bg-purple-700 p-4 rounded-lg shadow-md">
                <div>
                  <h3 className="text-2xl font-semibold text-cyan-300">{item.name}</h3>
                  <p className="text-lg text-cyan-400">${item.price.toFixed(2)}</p>
                </div>
                <div className="flex items-center space-x-4">
                  <button
                    onClick={() => updateQuantity(item.id, item.quantity - 1)}
                    className="px-3 py-1 bg-cyan-500 text-navy font-bold rounded-full"
                    disabled={item.quantity === 1}
                  >
                    -
                  </button>
                  <span className="text-lg font-semibold">{item.quantity}</span>
                  <button
                    onClick={() => updateQuantity(item.id, item.quantity + 1)}
                    className="px-3 py-1 bg-cyan-500 text-navy font-bold rounded-full"
                  >
                    +
                  </button>
                </div>
              </div>
            ))}
          </div>
          <div className="flex justify-between items-center bg-purple-800 p-6 rounded-lg shadow-md">
            <h2 className="text-2xl font-semibold text-cyan-500">Total:</h2>
            <p className="text-3xl font-bold text-cyan-400">${calculateTotalPrice()}</p>
          </div>
          <button className="w-full mt-6 bg-cyan-500 text-navy py-3 font-semibold rounded-lg shadow-lg hover:shadow-xl transform transition-all duration-300 hover:scale-105">
            Place Order
          </button>
        </>
      )}
    </section>
  );
};

export default Checkout;
