// src/components/CartDrawer.js
import React, { useState } from 'react';
import { useCart } from '../context/CartContext';
import { FaTrash } from 'react-icons/fa';

const CartDrawer = ({ isOpen, toggleCart }) => {
  const { cart, removeFromCart } = useCart();
  const [selectedItem, setSelectedItem] = useState(null);  // State for the selected item

  const handleItemClick = (item) => {
    setSelectedItem(item);  // Set the selected item for preview
  };

  const closeItemPreview = () => {
    setSelectedItem(null);  // Clear the selected item to close preview
  };

  return (
    <div
      className={`fixed bottom-0 left-0 w-full bg-purple-900 text-white transform transition-transform duration-300 ease-in-out ${isOpen ? 'translate-y-0' : 'translate-y-full'}`}
      style={{ maxHeight: '60vh', overflowY: 'auto' }}
    >
      <div className="p-4 flex justify-between items-center">
        <h2 className="text-xl font-bold">Your Cart</h2>
        <button onClick={toggleCart} className="text-cyan-500 text-lg">Close</button>
      </div>
      <div className="px-4 py-2 space-y-4">
        {cart.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          cart.map(item => (
            <div
              key={item.id}
              onClick={() => handleItemClick(item)}  // Show preview on item click
              className="flex justify-between items-center bg-purple-800 p-4 rounded-lg shadow-md cursor-pointer"
            >
              <div>
                <h3 className="font-semibold">{item.name}</h3>
                <p className="text-cyan-300">{item.price}</p>
                <p>Quantity: {item.quantity}</p>
              </div>
              <button onClick={(e) => { e.stopPropagation(); removeFromCart(item.id); }} className="text-red-500">
                <FaTrash />
              </button>
            </div>
          ))
        )}
      </div>
      {cart.length > 0 && (
        <div className="p-4 border-t border-purple-700">
          <button className="w-full bg-cyan-500 text-navy py-3 rounded-lg font-semibold" onClick={() => alert('Proceed to Checkout')}>
            Proceed to Checkout
          </button>
        </div>
      )}

      {/* Item Preview Modal */}
      {selectedItem && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center"
          onClick={closeItemPreview}
        >
          <div
            className="bg-white text-navy p-6 rounded-lg shadow-lg w-11/12 md:w-1/2 lg:w-1/3 relative"
            onClick={(e) => e.stopPropagation()}  // Prevent closing modal on inner click
          >
            <button onClick={closeItemPreview} className="absolute top-2 right-2 text-xl font-bold text-gray-500">
              &times;
            </button>
            <img
              src={selectedItem.image}
              alt={selectedItem.name}
              className="w-full h-48 object-contain mb-4"
            />
            <h3 className="text-2xl font-bold mb-2">{selectedItem.name}</h3>
            <p className="text-xl text-gray-700 mb-4">{selectedItem.price}</p>
            <p className="text-gray-600">{selectedItem.description}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default CartDrawer;
