import React, { useState } from 'react';
import { useCart } from '../context/CartContext';

const Checkout = () => {
  const { cart, updateQuantity } = useCart(); // Get cart and updateQuantity from context
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    email: '',
    address: '',
    city: '',
    zip: '',
  });
  const [isSubmitted, setIsSubmitted] = useState(false);

  if (cart.length === 0) {
    return <p className="text-center text-xl mt-8">Your cart is empty. Go back to the shop to add items!</p>;
  }

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCustomerInfo((prevInfo) => ({ ...prevInfo, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/api/checkout', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ customerInfo, cart }),
      });

      if (response.ok) {
        const orderData = await response.json();
        console.log('Order placed successfully:', orderData);
        setIsSubmitted(true);
      } else {
        const errorData = await response.json();
        console.error('Error placing order:', errorData);
        alert('Failed to place the order. Please try again.');
      }
    } catch (error) {
      console.error('Error during checkout:', error);
      alert('An error occurred. Please try again.');
    }
  };

  const calculateTotal = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0).toFixed(2);
  };

  return (
    <div className="max-w-4xl mx-auto p-8 bg-purple-800 text-white rounded-lg mt-8">
      <h1 className="text-3xl font-bold text-cyan-500 mb-6">Checkout</h1>

      {isSubmitted ? (
        <div className="text-center">
          <h2 className="text-2xl font-semibold text-cyan-300">Thank you for your order!</h2>
          <p className="text-lg text-gray-300">Your order has been submitted and is being processed by Printful.</p>
        </div>
      ) : (
        <>
          <div className="mb-8">
            <h2 className="text-2xl font-semibold mb-4">Order Summary</h2>
            <div className="space-y-4">
              {cart.map((item) => (
                <div
                  key={item.id}
                  className="flex justify-between items-center bg-purple-700 p-4 rounded-lg shadow-md"
                >
                  <div>
                    <h3 className="text-lg font-semibold text-cyan-300">{item.name}</h3>
                    <p className="text-cyan-400">${item.price.toFixed(2)}</p>
                    <div className="flex items-center space-x-4">
                      {/* Decrease Quantity Button */}
                      <button
                        onClick={() => updateQuantity(item.id, Math.max(item.quantity - 1, 1))}
                        className="px-3 py-1 bg-cyan-500 text-navy font-bold rounded-full"
                      >
                        -
                      </button>
                      {/* Quantity Display */}
                      <span className="text-lg font-semibold">{item.quantity}</span>
                      {/* Increase Quantity Button */}
                      <button
                        onClick={() => updateQuantity(item.id, item.quantity + 1)}
                        className="px-3 py-1 bg-cyan-500 text-navy font-bold rounded-full"
                      >
                        +
                      </button>
                    </div>
                  </div>
                  <p className="text-cyan-400">${(item.price * item.quantity).toFixed(2)}</p>
                </div>
              ))}
            </div>
            <div className="text-right mt-4 text-lg font-semibold text-cyan-300">
              Total: ${calculateTotal()}
            </div>
          </div>

          <form onSubmit={handleSubmit} className="space-y-6">
            <h2 className="text-2xl font-semibold mb-4">Customer Information</h2>
            <input
              type="text"
              name="name"
              placeholder="Full Name"
              value={customerInfo.name}
              onChange={handleChange}
              required
              className="w-full p-3 rounded-lg"
            />
            <input
              type="email"
              name="email"
              placeholder="Email Address"
              value={customerInfo.email}
              onChange={handleChange}
              required
              className="w-full p-3 rounded-lg"
            />
            <input
              type="text"
              name="address"
              placeholder="Street Address"
              value={customerInfo.address}
              onChange={handleChange}
              required
              className="w-full p-3 rounded-lg"
            />
            <input
              type="text"
              name="city"
              placeholder="City"
              value={customerInfo.city}
              onChange={handleChange}
              required
              className="w-full p-3 rounded-lg"
            />
            <input
              type="text"
              name="zip"
              placeholder="Zip Code"
              value={customerInfo.zip}
              onChange={handleChange}
              required
              className="w-full p-3 rounded-lg"
            />

            <button
              type="submit"
              className="w-full py-3 mt-6 bg-cyan-500 text-purple-800 font-bold rounded-full shadow-lg hover:shadow-xl"
            >
              Place Order
            </button>
          </form>
        </>
      )}
    </div>
  );
};

export default Checkout;
