import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // To get the product ID from the URL
import { useCart } from '../context/CartContext';

const ProductPage = () => {
  const { id } = useParams(); // Extract product ID from the URL
  const [product, setProduct] = useState(null);
  const [selectedVariant, setSelectedVariant] = useState(null);
  const { addToCart } = useCart();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/products/${id}`);
        const data = await response.json();
        setProduct(data);
        if (data.variants && data.variants.length > 0) {
          setSelectedVariant(data.variants[0]); // Set the default variant
        }
      } catch (error) {
        console.error('Error fetching product:', error);
      }
    };
    fetchProduct();
  }, [id]);

  const handleVariantChange = (variant) => {
    setSelectedVariant(variant);
  };

  const handleAddToCart = () => {
    if (!selectedVariant) {
      alert('No variant selected.');
      return;
    }
    addToCart({
      id: product.id,
      name: product.name,
      price: selectedVariant.price,
      thumbnail_url: product.thumbnail_url,
      variant_id: selectedVariant.id,
      quantity: 1,
    });
  };

  if (!product) {
    return <p>Loading product details...</p>;
  }

  return (
    <div className="max-w-4xl mx-auto p-6 bg-background text-textcolor rounded-lg shadow-lg">
      <div className="flex flex-col md:flex-row">
        {/* Product Image */}
        <img
          src={product.thumbnail_url || 'https://via.placeholder.com/300'}
          alt={product.name}
          className="w-full md:w-1/2 rounded-lg object-cover"
        />

        {/* Product Details */}
        <div className="flex flex-col p-4">
          <h1 className="text-3xl font-bold text-primary">{product.name}</h1>
          <p className="text-lg text-secondary mt-2">{product.description}</p>
          <p className="text-xl font-semibold mt-4">
            ${selectedVariant ? (selectedVariant.price / 100).toFixed(2) : 'Price Unavailable'}
          </p>

          {/* Variant Selector */}
          {product.variants && product.variants.length > 0 ? (
            <div className="mt-4">
              <label className="block text-sm font-bold mb-2">Choose Variant:</label>
              <select
                className="w-full px-4 py-2 border rounded-lg"
                value={selectedVariant?.id || ''}
                onChange={(e) =>
                  handleVariantChange(
                    product.variants.find((variant) => variant.id === e.target.value)
                  )
                }
              >
                {product.variants.map((variant) => (
                  <option key={variant.id} value={variant.id}>
                    {variant.name} - ${(variant.price / 100).toFixed(2)}
                  </option>
                ))}
              </select>
            </div>
          ) : (
            <button
              className="mt-4 bg-gray-500 text-white px-4 py-2 rounded-lg cursor-not-allowed"
              disabled
            >
              No Variants Available
            </button>
          )}

          {/* Add to Cart Button */}
          <button
            onClick={handleAddToCart}
            className={`mt-6 px-6 py-3 rounded-lg text-white ${
              selectedVariant ? 'bg-primary hover:bg-secondary' : 'bg-gray-500 cursor-not-allowed'
            }`}
            disabled={!selectedVariant}
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
