// src/components/ProductCard.js
import React from 'react';
import { Link } from 'react-router-dom';

const ProductCard = ({ product }) => {
  return (
    <div className="bg-white rounded-lg shadow-lg overflow-hidden transform transition duration-300 hover:scale-105">
      <div className="relative">
        <img
          src={product.image}
          alt={product.name}
          className="h-64 w-full object-cover"
        />
        <div className="absolute inset-0 bg-gradient-to-t from-transparent to-black opacity-50"></div>
        <h3 className="absolute bottom-4 left-4 text-white text-2xl font-bold">
          {product.name}
        </h3>
      </div>
      <div className="p-4">
        <p className="text-lg font-semibold text-[#593C8F]">
          ${product.price}
        </p>
        <Link to={`/product/${product.id}`}>
          <button className="mt-4 w-full py-2 bg-[#DB5461] text-white font-medium rounded hover:bg-[#171738] transition-colors duration-300">
            View Details
          </button>
        </Link>
      </div>
    </div>
  );
};

export default ProductCard;
