// frontend/src/context/CartContext.js

import React, { createContext, useState, useContext } from 'react';

const CartContext = createContext();

export const useCart = () => useContext(CartContext);

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);

  const addToCart = (product) => {
    // Extract necessary properties from the product
    const productImage = product.thumbnail_url || 'default_image_url';
    const productName = product.name || 'Product Name';
    const productPrice =
      (product.variants && product.variants.length > 0 && parseFloat(product.variants[0].retail_price)) ||
      (product.sync_variants && product.sync_variants.length > 0 && parseFloat(product.sync_variants[0].retail_price)) ||
      0.0;

    // Create a cart item with the necessary properties
    const cartItem = {
      id: product.id,
      name: productName,
      image: productImage,
      price: productPrice,
      quantity: 1, // Start with quantity 1
    };

    // Check if the product is already in the cart
    const existingItem = cart.find((item) => item.id === cartItem.id);

    if (existingItem) {
      // Update quantity
      setCart(
        cart.map((item) =>
          item.id === cartItem.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        )
      );
    } else {
      // Add new item
      setCart([...cart, cartItem]);
    }
  };

  const removeFromCart = (productId) => {
    setCart(cart.filter((item) => item.id !== productId));
  };

  // Optional: Function to calculate the total price of the cart
  const getTotalPrice = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  return (
    <CartContext.Provider value={{ cart, addToCart, removeFromCart, getTotalPrice }}>
      {children}
    </CartContext.Provider>
  );
};

export default CartContext;
