// src/components/ProductPage.js
import React from 'react';
import { useParams } from 'react-router-dom';
import { products } from '../data';

const ProductPage = () => {
  const { productId } = useParams();
  const product = products.find((p) => p.id === parseInt(productId));

  if (!product) return <p className="text-center">Product not found</p>;

  return (
    <section className="bg-white text-navy min-h-screen p-8">
      <div className="max-w-4xl mx-auto flex flex-col md:flex-row items-center gap-8">
        
        {/* Product Image */}
        <img src={product.image} alt={product.name} className="w-full md:w-1/2 rounded-lg shadow-lg" />

        {/* Product Details */}
        <div className="md:w-1/2">
          <h1 className="text-4xl font-bold mb-4">{product.name}</h1>
          <p className="text-2xl font-semibold text-purple mb-6">{product.price}</p>
          <p className="text-lg mb-8">
            Hereâ€™s where you can add a more detailed description of the product, including material, fit, style, and unique features.
          </p>
          <button className="px-8 py-3 bg-cyan text-navy font-semibold rounded-lg shadow-lg hover:shadow-xl transform transition-all duration-300 hover:scale-105">
            Add to Cart
          </button>
        </div>

      </div>
    </section>
  );
};

export default ProductPage;
