// src/components/LoadingScreen.js
import React, { useEffect, useState } from 'react';
import './LoadingScreen.css';

const LoadingScreen = ({ onComplete }) => {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoading(false);
      setTimeout(() => {
        onComplete(); // Trigger the onComplete function after fade-out and slide transition
      }, 1500); // Allow more time for smoother exit
    }, 3000); // Initial loading time

    return () => clearTimeout(timer);
  }, [onComplete]);

  return (
    <div className={`fixed inset-0 z-50 loading-screen ${isLoading ? 'loading' : 'loaded'}`}>
      {/* Left and Right Panels */}
      <div className="loading-panel loading-panel-left"></div>
      <div className="loading-panel loading-panel-right"></div>

      {/* Loader */}
      {isLoading && (
        <div className="loader-container">
          <div className="sk-folding-cube">
            <div className="sk-cube1 sk-cube"></div>
            <div className="sk-cube2 sk-cube"></div>
            <div className="sk-cube4 sk-cube"></div>
            <div className="sk-cube3 sk-cube"></div>
          </div>
        </div>
      )}
    </div>
  );
};

export default LoadingScreen;
