import React, { useState } from 'react';
import { useCart } from '../context/CartContext';

const Checkout = () => {
  const { cart, getTotalPrice } = useCart(); // Access cart and total price from context
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    email: '',
    address: '',
    city: '',
    state: '',
    zip: '',
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCustomerInfo((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (cart.length === 0) {
      alert('Your cart is empty.');
      return;
    }

    try {
      setIsSubmitting(true);

      // Call backend to create Stripe session
      const response = await fetch('http://localhost:5000/api/stripe/create-checkout-session', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ cart, customerInfo }),
      });

      const data = await response.json();

      if (response.ok) {
        // Redirect to Stripe Checkout
        window.location.href = `https://checkout.stripe.com/pay/${data.id}`;
      } else {
        console.error('Error creating Stripe session:', data.error);
        alert(`Checkout failed: ${data.error}`);
      }
    } catch (error) {
      console.error('Checkout Error:', error.message);
      alert('An error occurred during checkout.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const calculateTotal = () => getTotalPrice().toFixed(2);

  return (
    <div className="max-w-4xl mx-auto p-8 bg-gray-100 text-gray-800 rounded-lg mt-8 shadow-md">
      <h1 className="text-3xl font-bold text-gray-700 mb-6">Checkout</h1>

      <form onSubmit={handleSubmit}>
        {/* Order Summary */}
        <div className="mb-8">
          <h2 className="text-2xl font-semibold mb-4">Order Summary</h2>
          <div className="bg-white shadow rounded-lg p-4 space-y-4">
            {cart.map((item) => (
              <div key={item.id} className="flex items-center space-x-4">
                <img
                  src={item.image || 'https://via.placeholder.com/100'}
                  alt={item.name}
                  className="w-20 h-20 object-contain rounded-lg border"
                />
                <div className="flex-grow">
                  <h3 className="text-lg font-medium">{item.name}</h3>
                  <p className="text-sm text-gray-600">Quantity: {item.quantity}</p>
                </div>
                <p className="text-lg font-semibold">${(item.price * item.quantity).toFixed(2)}</p>
              </div>
            ))}
            <div className="border-t border-gray-300 pt-4 text-right text-lg font-semibold">
              Total: ${calculateTotal()}
            </div>
          </div>
        </div>

        {/* Customer Information */}
        <div className="mb-8">
          <h2 className="text-2xl font-semibold mb-4">Customer Information</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <input
              type="text"
              name="name"
              placeholder="Full Name"
              value={customerInfo.name}
              onChange={handleChange}
              required
              className="w-full p-3 rounded-lg border border-gray-300 bg-white focus:outline-none focus:ring-2 focus:ring-purple-500"
            />
            <input
              type="email"
              name="email"
              placeholder="Email Address"
              value={customerInfo.email}
              onChange={handleChange}
              required
              className="w-full p-3 rounded-lg border border-gray-300 bg-white focus:outline-none focus:ring-2 focus:ring-purple-500"
            />
            <input
              type="text"
              name="address"
              placeholder="Street Address"
              value={customerInfo.address}
              onChange={handleChange}
              required
              className="w-full p-3 rounded-lg border border-gray-300 bg-white focus:outline-none focus:ring-2 focus:ring-purple-500"
            />
            <input
              type="text"
              name="city"
              placeholder="City"
              value={customerInfo.city}
              onChange={handleChange}
              required
              className="w-full p-3 rounded-lg border border-gray-300 bg-white focus:outline-none focus:ring-2 focus:ring-purple-500"
            />
            <input
              type="text"
              name="state"
              placeholder="State"
              value={customerInfo.state}
              onChange={handleChange}
              required
              className="w-full p-3 rounded-lg border border-gray-300 bg-white focus:outline-none focus:ring-2 focus:ring-purple-500"
            />
            <input
              type="text"
              name="zip"
              placeholder="Zip Code"
              value={customerInfo.zip}
              onChange={handleChange}
              required
              className="w-full p-3 rounded-lg border border-gray-300 bg-white focus:outline-none focus:ring-2 focus:ring-purple-500"
            />
          </div>
        </div>

        {/* Submit Button */}
        <button
          type="submit"
          disabled={isSubmitting}
          className={`w-full py-3 ${
            isSubmitting ? 'bg-gray-400 cursor-not-allowed' : 'bg-purple-500 hover:bg-purple-600'
          } text-white font-bold rounded-lg shadow-lg focus:outline-none focus:ring-4 focus:ring-purple-300 transition-all`}
        >
          {isSubmitting ? 'Processing...' : 'Proceed to Payment'}
        </button>
      </form>
    </div>
  );
};

export default Checkout;
