import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';

const ProductCard = ({ product }) => {
  const { addToCart } = useCart();

  const handleAddToCart = () => {
    if (!product.variant_id) {
      alert('Product variant_id is not available');
      return;
    }

    addToCart({
      id: product.id,
      name: product.name,
      price: product.price,
      thumbnail_url: product.thumbnail_url,
      variant_id: product.variant_id,
      quantity: 1,
    });
  };

  return (
    <div 
      className="relative w-full h-auto rounded-lg border border-black bg-white 
                 transition-transform duration-300 hover:scale-105 hover:-rotate-1 
                 overflow-hidden"
      style={{
        // A subtle transform origin to enhance the 3D hover effect
        transformOrigin: 'center center'
      }}
    >
      {/* Product Image */}
      <Link to={`/product/${product.id}`}>
        <img
          src={product.thumbnail_url || 'https://via.placeholder.com/150'}
          alt={product.name}
          className="w-full h-64 object-cover rounded-t-lg"
        />
      </Link>

      {/* Bottom Panel: Product Details + Actions */}
      {/* This panel is normally partially hidden and slides up on hover */}
      <div 
        className="absolute bottom-0 left-0 right-0 bg-white border-t border-black 
                   px-4 py-3 translate-y-1/2 group-hover:translate-y-0 
                   transition-transform duration-300 flex flex-col items-start space-y-2"
        style={{
          // Panel is initially halfway down, moves up fully on hover due to parent hover
          transform: 'translateY(50%)'
        }}
      >
        <h3 className="text-lg font-semibold text-black whitespace-nowrap overflow-ellipsis overflow-hidden max-w-full">
          {product.name}
        </h3>
        <p className="text-base text-black">
          {product.price ? `$${(product.price / 100).toFixed(2)}` : 'Price Unavailable'}
        </p>

        <div className="flex space-x-2 mt-2">
          {/* Add to Cart Button */}
          <button
            onClick={handleAddToCart}
            className="px-3 py-1 border border-black bg-white font-semibold 
                       hover:bg-black hover:text-white transition-colors duration-300"
          >
            Add to Cart
          </button>

          {/* View Details Button */}
          <Link
            to={`/product/${product.id}`}
            className="px-3 py-1 border border-black bg-white font-semibold 
                       hover:bg-black hover:text-white transition-colors duration-300"
          >
            View Details
          </Link>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
