// frontend/src/pages/ShopPage.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SearchBar from '../components/SearchBar';
import Filter from '../components/Filter';
import Sort from '../components/Sort';
import Pagination from '../components/Pagination';
import ProductCard from '../components/ProductCard';

const ShopPage = () => {
  const [products, setProducts] = useState([]); // Original list of products
  const [filteredProducts, setFilteredProducts] = useState([]); // Filtered and sorted products
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [sortOption, setSortOption] = useState('');
  const [currentPage, setCurrentPage] = useState(1);

  const productsPerPage = 6;

  // Fetch products from the backend API
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        // Fetch the list of products
        const response = await axios.get('http://localhost:5000/api/products');
        console.log('API Response:', response.data);

        const products = response.data.result || [];
        console.log('Fetched Products:', products);

        // Fetch variants for each product
        const productsWithVariants = await Promise.all(
          products.map(async (product) => {
            try {
              const productDetailsResponse = await axios.get(
                `http://localhost:5000/api/products/${product.id}`
              );

              // Merge variant details into the product
              return {
                ...product,
                variants: productDetailsResponse.data.result.sync_variants,
              };
            } catch (error) {
              console.error(`Error fetching variants for product ${product.id}:`, error);
              return product; // Return the product without variants
            }
          })
        );

        setProducts(productsWithVariants);
        setFilteredProducts(productsWithVariants);
      } catch (error) {
        console.error('Error fetching products:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  // Filter and sort products
  useEffect(() => {
    // Start with the original list of products
    let updatedProducts = [...products];
    console.log('Original Products:', updatedProducts); // Log the original products before filtering

    // Filter by search term
    if (searchTerm) {
      updatedProducts = updatedProducts.filter((product) =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
      console.log('After Search Filter:', updatedProducts); // Log after search filtering
    }

    // Filter by selected category
    if (selectedCategory) {
      updatedProducts = updatedProducts.filter(
        (product) => product.category === selectedCategory
      );
      console.log('After Category Filter:', updatedProducts); // Log after category filtering
    }

    // Sorting logic
    if (sortOption === 'priceLowHigh') {
      updatedProducts.sort(
        (a, b) =>
          parseFloat(a.variants?.[0]?.retail_price || 0) -
          parseFloat(b.variants?.[0]?.retail_price || 0)
      );
    } else if (sortOption === 'priceHighLow') {
      updatedProducts.sort(
        (a, b) =>
          parseFloat(b.variants?.[0]?.retail_price || 0) -
          parseFloat(a.variants?.[0]?.retail_price || 0)
      );
    } else if (sortOption === 'nameAsc') {
      updatedProducts.sort((a, b) => a.name.localeCompare(b.name));
    } else if (sortOption === 'nameDesc') {
      updatedProducts.sort((a, b) => b.name.localeCompare(a.name));
    }
    console.log('After Sorting:', updatedProducts); // Log after sorting

    setFilteredProducts(updatedProducts);
  }, [products, searchTerm, selectedCategory, sortOption]);

  // Pagination logic
  const indexOfLastProduct = currentPage * productsPerPage;
  const indexOfFirstProduct = indexOfLastProduct - productsPerPage;

  // Ensure filteredProducts is an array before slicing
  const currentProducts = Array.isArray(filteredProducts)
    ? filteredProducts.slice(indexOfFirstProduct, indexOfLastProduct)
    : [];

  console.log('Current Products for Page:', currentProducts); // Log the products for the current page

  const totalPages = Math.ceil(
    Array.isArray(filteredProducts) ? filteredProducts.length / productsPerPage : 0
  );

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
    setCurrentPage(1);
  };

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
    setCurrentPage(1);
  };

  const handleSortChange = (event) => {
    setSortOption(event.target.value);
  };

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  if (loading) return <p>Loading products...</p>;
  if (!loading && products.length === 0)
    return <p>No products available or failed to load.</p>;

  return (
    <div className="bg-purple min-h-screen">
      <section className="max-w-6xl mx-auto p-8 text-white">
        <h1 className="text-5xl font-bold mb-2 text-pink text-center">
          Welcome to Our Shop
        </h1>
        <p className="text-xl mb-8 text-cyan text-center">
          Explore our exclusive collection of hats
        </p>

        <SearchBar searchTerm={searchTerm} onSearchChange={handleSearchChange} />
        <div className="flex flex-col md:flex-row md:space-x-4 mb-6">
          <Filter
            categories={[
              ...new Set(products.map((product) => product.category || 'Others')),
            ]}
            selectedCategory={selectedCategory}
            onSelectCategory={handleCategoryChange}
          />
          <Sort sortOption={sortOption} onSortChange={handleSortChange} />
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {currentProducts.map((product) => (
            <ProductCard key={product.id} product={product} />
          ))}
        </div>

        {totalPages > 1 && (
          <Pagination
            currentPage={currentPage}
            totalPages={totalPages}
            onPageChange={handlePageChange}
          />
        )}
      </section>
    </div>
  );
};

export default ShopPage;
