import React from 'react';
import SearchBar from '../components/SearchBar';
import Filter from '../components/Filter';
import Sort from '../components/Sort';
import Pagination from '../components/Pagination';
import ProductList from '../components/ProductList'; // Import ProductList instead of handling fetch here

const ShopPage = () => {
  return (
    <div className="bg-purple min-h-screen">
      <section className="max-w-6xl mx-auto p-8 text-white">
        <h1 className="text-5xl font-bold mb-2 text-pink text-center">
          Welcome to Our Shop
        </h1>
        <p className="text-xl mb-8 text-cyan text-center">
          Explore our exclusive collection of hats
        </p>

        <SearchBar />
        <div className="flex flex-col md:flex-row md:space-x-4 mb-6">
          <Filter />
          <Sort />
        </div>

        {/* Render ProductList which now handles fetching products */}
        <ProductList />

        {/* Optionally, you could add Pagination here if needed */}
      </section>
    </div>
  );
};

export default ShopPage;
