// src/components/FeaturedProducts.js
import React, { useState, useEffect } from 'react';
import ProductCard from './ProductCard';
import axios from 'axios';

const FeaturedProducts = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);

  useEffect(() => {
    const fetchFeaturedProducts = async () => {
      try {
        const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;
        const response = await axios.get(`${API_BASE_URL}/api/products`);
        console.log("Featured Products Data:", response.data);
        // Adjust slicing as needed based on your API
        setFeaturedProducts(response.data.products.slice(0, 3));
      } catch (error) {
        console.error('Error fetching featured products:', error);
      }
    };

    fetchFeaturedProducts();
  }, []);

  return (
    <section 
      className="py-12 px-4 sm:py-16 sm:px-8 relative"
      style={{
        background: '#A5AE9E', // Ash Gray background
        color: '#2F2504', // Dark Drab Brown text
      }}
    >
      <h2 
        className="text-3xl sm:text-4xl font-extrabold mb-8 text-center relative inline-block"
        
      >
        Featured Products
      </h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
        {featuredProducts.map((product) => (
          <div
            key={product.id}
            className="relative bg-white p-4 rounded-lg"
            style={{
              // Hard, no-blur shadow around each product card
              boxShadow: '10px 10px 0 #000',
            }}
          >
            <ProductCard product={product} />
          </div>
        ))}
      </div>
    </section>
  );
};

export default FeaturedProducts;
