// src/components/LoadingScreen.js
import React, { useEffect, useState } from 'react';
import './LoadingScreen.css';

const LoadingScreen = ({ onComplete }) => {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Start the door split immediately on load
    const timer = setTimeout(() => {
      setIsLoading(false); // Triggers the door split and loader fade-out
      setTimeout(() => {
        onComplete(); // Trigger onComplete function after fade-out completes
      }, 2300); // Fade and door transition total duration
    }, 1500); // Delay reduced for immediate start

    return () => clearTimeout(timer);
  }, [onComplete]);

  return (
    <div className={`loading-screen ${!isLoading ? 'loaded' : ''}`}>
      <div className="left-door"></div>
      <div className="right-door"></div>
      <div className="loader-container">
        <div className="sk-folding-cube">
          <div className="sk-cube1 sk-cube"></div>
          <div className="sk-cube2 sk-cube"></div>
          <div className="sk-cube4 sk-cube"></div>
          <div className="sk-cube3 sk-cube"></div>
        </div>
      </div>
    </div>
  );
};

export default LoadingScreen;
