// src/components/Carousel.js
import React, { useState } from 'react';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa';

const Carousel = ({ images }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  return (
    <div className="relative w-full flex justify-center items-center">
      {/* Carousel Images */}
      <div className="relative w-full h-80 flex items-center justify-center">
        {images.map((image, index) => (
          <img
            key={index}
            src={image}
            alt={`Slide ${index + 1}`}
            className={`absolute w-full h-full object-cover rounded-lg shadow-lg transition-transform duration-500 ${
              index === currentIndex ? 'z-10 opacity-100 scale-100' : 'z-0 opacity-0 scale-75'
            }`}
            style={{
              transform: index === currentIndex ? 'scale(1)' : 'scale(0.75)',
              opacity: index === currentIndex ? 1 : 0,
              transition: 'opacity 0.5s ease, transform 0.5s ease',
            }}
          />
        ))}
      </div>

      {/* Left Arrow */}
      <button
        onClick={handlePrev}
        className="absolute left-0 top-1/2 transform -translate-y-1/2 text-cyan-500 hover:text-cyan-300 text-2xl p-2"
      >
        <FaArrowLeft />
      </button>

      {/* Right Arrow */}
      <button
        onClick={handleNext}
        className="absolute right-0 top-1/2 transform -translate-y-1/2 text-cyan-500 hover:text-cyan-300 text-2xl p-2"
      >
        <FaArrowRight />
      </button>
    </div>
  );
};

export default Carousel;
