import React, { useState } from 'react';

const Checkout = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    // Example cart for testing
    const cart = [
      {
        name: "Shiba Inu Sunglasses Dad Hat",
        price: 1600, // Price in cents
        thumbnail_url: "https://files.cdn.printful.com/files/cb5/cb5e29ebabb11d899ec9125a242a76d0_preview.png",
        quantity: 1,
        variant_id: 4619258269,
      },
    ];

    console.log('Cart being sent to the server:', JSON.stringify(cart, null, 2));

    try {
      const response = await fetch('http://localhost:5000/api/stripe/create-checkout-session', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ cart }),
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Checkout API Response Error:', errorText);
        throw new Error(`Error: ${response.status} - ${errorText}`);
      }

      const data = await response.json();
      console.log('Stripe Checkout Session Response:', data);

      if (data && data.id) {
        console.log(`Redirecting to Stripe Checkout: ${data.id}`);
        window.location.href = `https://checkout.stripe.com/pay/${data.id}`;
      } else {
        throw new Error('Stripe session ID missing in the response.');
      }
    } catch (err) {
      console.error('Error during checkout process:', err.message);
      setError(err.message || 'Something went wrong during the checkout process.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>Checkout</h1>
      {error && (
        <div style={{ color: 'red', marginBottom: '1rem' }}>
          <strong>Error:</strong> {error}
        </div>
      )}
      <button onClick={handleSubmit} disabled={loading}>
        {loading ? 'Processing...' : 'Checkout'}
      </button>
    </div>
  );
};

export default Checkout;
