// src/components/Carousel.js
import React, { useState } from 'react';
import { FaArrowLeft, FaArrowRight } from 'react-icons/fa';

const Carousel = ({ images }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Move to the next image
  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  // Move to the previous image
  const handlePrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  return (
    <div className="relative w-full max-h-[500px] flex justify-center items-center">
      {/* Display images with object-contain to ensure the whole image fits */}
      <div className="relative w-full h-[400px] flex items-center justify-center overflow-hidden">
        {images.map((image, index) => (
          <img
            key={index}
            src={image}
            alt={`Slide ${index + 1}`}
            className={`absolute max-h-[400px] w-auto object-contain transition-opacity duration-500 ${
              index === currentIndex ? 'opacity-100' : 'opacity-0'
            }`}
            style={{
              zIndex: index === currentIndex ? 10 : 0,
              transition: 'opacity 0.5s ease-in-out, transform 0.5s ease-in-out',
            }}
          />
        ))}
      </div>

      {/* Left Arrow */}
      <button
        onClick={handlePrev}
        className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-purple-800 text-white rounded-full p-2 hover:bg-purple-600 z-20"
      >
        <FaArrowLeft size={20} />
      </button>

      {/* Right Arrow */}
      <button
        onClick={handleNext}
        className="absolute right-4 top-1/2 transform -translate-y-1/2 bg-purple-800 text-white rounded-full p-2 hover:bg-purple-600 z-20"
      >
        <FaArrowRight size={20} />
      </button>
    </div>
  );
};

export default Carousel;
