// src/components/Navbar.js
import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import HamburgerMenu from './HamburgerMenu';

const Navbar = () => {
  const { cart } = useCart();
  const cartItemCount = cart.reduce((total, item) => total + item.quantity, 0);

  return (
    <nav className="bg-purple-900 text-white p-4 flex justify-between items-center">
      <div className="text-2xl font-bold">
        <Link to="/">Dad Hat Hub</Link>
      </div>

      {/* Desktop Links */}
      <div className="hidden md:flex space-x-8">
        <Link to="/" className="hover:text-cyan-500">Home</Link>
        <Link to="/shop" className="hover:text-cyan-500">Shop</Link>
        <Link to="/checkout" className="hover:text-cyan-500">Checkout</Link>
        <Link to="/cart" className="relative">
          Cart
          {cartItemCount > 0 && (
            <span className="absolute -top-2 -right-4 bg-black text-white text-xs font-bold rounded-full w-6 h-6 flex items-center justify-center">
              {cartItemCount}
            </span>
          )}
        </Link>
      </div>

      {/* Mobile Hamburger Menu */}
      <HamburgerMenu />
    </nav>
  );
};

export default Navbar;
