import React, { useEffect, useState } from 'react';
import './LoadingScreen.css';

const LoadingScreen = ({ onComplete }) => {
  const [isLoading, setIsLoading] = useState(true);
  const [isPaused, setIsPaused] = useState(false);

  useEffect(() => {
    if (!isPaused) {
      // Start the animation when isPaused is false
      const timer = setTimeout(() => {
        setIsLoading(false);
        setTimeout(() => {
          onComplete();
        }, 2500);
      }, 1500);

      return () => clearTimeout(timer);
    }
  }, [isPaused, onComplete]);

  const togglePause = () => {
    setIsPaused(!isPaused);
    document.querySelectorAll('.loading-screen, .left-door, .right-door, .loader')
      .forEach(el => el.style.animationPlayState = isPaused ? 'running' : 'paused');
  };

  return (
    <div className={`loading-screen ${!isLoading ? 'loaded' : ''}`}>
      <button onClick={togglePause} className="debug-pause-button">Toggle Pause</button>
      <div className="left-door"></div>
      <div className="right-door"></div>
      <div className="loader-container">
        <div className="loader"></div>
      </div>
    </div>
  );
};

export default LoadingScreen;
