// src/components/CartDrawer.js
import React from 'react';
import { useCart } from '../context/CartContext';
import { FaTrash } from 'react-icons/fa';

const CartDrawer = ({ isOpen, toggleCart }) => {
  const { cart, removeFromCart } = useCart();

  return (
    <div
      className={`fixed bottom-0 left-0 w-full bg-purple-900 text-white transform transition-transform duration-300 ease-in-out ${isOpen ? 'translate-y-0' : 'translate-y-full'}`}
      style={{ maxHeight: '60vh', overflowY: 'auto' }}
    >
      <div className="p-4 flex justify-between items-center">
        <h2 className="text-xl font-bold">Your Cart</h2>
        <button onClick={toggleCart} className="text-cyan-500 text-lg">Close</button>
      </div>
      <div className="px-4 py-2 space-y-4">
        {cart.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          cart.map(item => (
            <div key={item.id} className="flex justify-between items-center bg-purple-800 p-4 rounded-lg shadow-md">
              <div>
                <h3 className="font-semibold">{item.name}</h3>
                <p className="text-cyan-300">{item.price}</p>
                <p>Quantity: {item.quantity}</p>
              </div>
              <button onClick={() => removeFromCart(item.id)} className="text-red-500">
                <FaTrash />
              </button>
            </div>
          ))
        )}
      </div>
      {cart.length > 0 && (
        <div className="p-4 border-t border-purple-700">
          <button className="w-full bg-cyan-500 text-navy py-3 rounded-lg font-semibold" onClick={() => alert('Proceed to Checkout')}>
            Proceed to Checkout
          </button>
        </div>
      )}
    </div>
  );
};

export default CartDrawer;
