// src/components/Navbar.js
import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { FaShoppingCart } from 'react-icons/fa';

const Navbar = () => {
  const { cart } = useCart();

  // Calculate the total item count in the cart
  const cartItemCount = cart.reduce((total, item) => total + item.quantity, 0);

  return (
    <nav className="bg-navy p-4 text-white flex justify-between items-center">
      <Link to="/" className="text-2xl font-bold">Dad Hat Hub</Link>
      
      <div className="flex space-x-6 items-center">
        <Link to="/">Home</Link>
        
        {/* Cart Icon with Badge */}
        <Link to="/cart" className="relative flex items-center">
          <FaShoppingCart size={24} />

          {cartItemCount > 0 && (
            <span className="absolute -top-2 -right-2 bg-black bg-opacity-70 text-white rounded-full text-xs w-6 h-6 flex items-center justify-center">
              {cartItemCount}
            </span>
          )}
        </Link>
      </div>
    </nav>
  );
};

export default Navbar;
